var Code = function Code(code, scope) {
  if (!(this instanceof Code)) return new Code(code, scope);
  this._bsontype = 'Code';
  this.code = code;
  this.scope = scope;
};
Code.prototype.toJSON = function() {
  return { scope: this.scope, code: this.code };
};
module.exports = Code;
module.exports.Code = Code;
