"use strict";
var Typpy = require("typpy");
function Deffy(input, def, options) {
    if (typeof def === "function") {
        return def(input);
    }
    options = Typpy(options) === "boolean" ? {
        empty: options
    } : {
        empty: false
    };
    if (options.empty) {
        return input || def;
    }
    if (Typpy(input) === Typpy(def)) {
        return input;
    }
    return def;
}
module.exports = Deffy;
