'use strict';
function asyncIterator() {
  const cursor = this;
  return {
    next: function() {
      return Promise.resolve()
        .then(() => cursor.next())
        .then(value => {
          if (!value) {
            return cursor.close().then(() => ({ value, done: true }));
          }
          return { value, done: false };
        });
    }
  };
}
exports.asyncIterator = asyncIterator;
