"use strict";
var Ul = require("ul"),
    IterateObject = require("iterate-object");
var OArgv = module.exports = function (options, prgm, stringify) {
    var sOpts = [],
        c = null,
        escape = JSON.stringify;
    if (typeof prgm === "boolean") {
        stringify = prgm;
        prgm = null;
    }
    options = Ul.merge(options, {
        _: [],
        __: OArgv.separator
    });
    if (typeof options._ === "string") {
        options._ = [options._];
    }
    IterateObject(options, function (c, k) {
        if (k === "_" || k === "__") {
            return;
        }
        var cArg = (k.length === 1 ? "-" : "--") + k;
        if (c === true) {
            sOpts.push(cArg);
            return;
        }
        if (typeof c === "string" && !Array.isArray(c)) {
            c = [c];
        }
        IterateObject(c, function (cc) {
            var ccArg = cArg;
            if (options.__ !== " ") {
                ccArg += options.__ + cc;
                sOpts.push(ccArg);
            } else {
                sOpts.push(ccArg, cc);
            }
        });
    });
    if (stringify) {
        return (prgm ? prgm + " " : "") + sOpts.map(function (c) {
            return escape(c);
        }).join(" ") + (options._.length ? " " + options._.map(function (c) {
            return escape(c);
        }).join(" ") : "");
    } else {
        if (typeof prgm === "string") {
            sOpts.unshift(prgm);
        }
        sOpts = sOpts.concat(options._);
        return sOpts;
    }
};
OArgv.separator = " ";
