"use strict";
var Fs = require("fs");
function rJson(path, callback) {
    if (typeof callback === "function") {
        Fs.readFile(path, "utf-8", function (err, data) {
            try {
                data = JSON.parse(data);
            } catch (e) {
                err = err || e;
            }
            callback(err, data);
        });
        return;
    }
    return JSON.parse(Fs.readFileSync(path));
}
module.exports = rJson;
